public void evaluate(Individual<BooleanChromosome> individual) {
    BooleanChromosome chrom = individual.getChromosome();
    int count = 0;
    int length=chrom.length();
    for(int i=0;i<length;i++)
        if(chrom.getValue(i))
            count++;

